import React, { useEffect, useState } from 'react';
import './About.css';

function About() {
  const [showSection, setShowSection] = useState(false);

  useEffect(() => {
    setShowSection(true);
  }, []);

  return (
    <section id='about' className={`about ${showSection ? 'section-show' : ''}`}>
      <h1>About</h1>
      <p>This is the About page.</p>
    </section>
  );
}

export default About;


