import React from 'react'
import './Certificate.css';

function Certificate() {
  const certificates = [
    {
      institution: "Google",
      name: "Google Cloud Platform Fundamentals",
      date: "2024-07-15",
      link: "https://cloud.google.com/certification/cloud-platform-fundamentals",
    },
    {
      institution: "Coursera",
      name: "The Science of Data Science",
      date: "2023-12-01",
    },
  ];
  return (
    <section id='certificate' className='certificate'>
      <div className='container certificate-me'>
        <div className='heading-wrapper'>
          <h2>Certificate</h2>
        </div>
      </div>
      <div>
        <ul>
          {certificates.map((cert, index) => (
            <li key={index}>
              <div className="cert-details">
                {cert.institution && (
                  <img
                    src={`path/to/logo/${cert.institution.toLowerCase()}.png`}
                    alt={`${cert.institution} Logo`}
                    className="cert-logo"
                  />
                )}
                <div>
                  <h4>{cert.name}</h4>
                  {cert.date && <p>Completed: {cert.date}</p>}
                  {cert.link && <a href={cert.link} target="_blank" rel="noreferrer">View Certificate</a>}
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </section>
  )
}

export default Certificate
