import React from 'react';
import './Certificate.css';
import collagePoject from 'src/images/clgProject Certificate.jpg';
import Dance from 'src/images/dance certificate.jpg';
import FrontEnd_Course from '';
import FrontEnd_Developer from '';
import Garba from '';
import Hexolt from '';
import javascript from '';
import mahindra_pride from '';
import skillVertex from '';
import Upskill from '';

const Certificate = () => {
  return (
    <section id='certificate' className='certificate'>
      <div className='container certificate-me'>

        {/* Certificate Section */}
        <div className='heading-wrapper'>
          <h2>Certificate</h2>
        </div>

        <div className='row gy-4'>
          {/* Certificate Cards */}
          {certificateData.map((certificate, index) => (
            <div key={index} className='col-md-6' data-aos='fade-up' data-aos-delay={index * 300}>
              <div className='card-custom rounded-4 bg-base shadow-effect'>
                <div className='card-custom-image rounded-4'>
                  <img className='rounded-4' src={certificate.image} alt={certificate.title} />
                </div>
                <div className='card-custom-content p-4'>
                  <h4>{certificate.title}</h4>
                  <p>{certificate.description}</p>
                  <a href={certificate.link} className='custom-link'>{certificate.linkText}</a>
                </div>
              </div>
            </div>
          ))}
        </div>

        {/* Extra Curriculum Section */}
        <div className='row pb-4' data-aos='fade-up'>
          <div className='col-lg-8'>
            <h1 className='p-5'>My Extra Curricular Certificates</h1>
          </div>
        </div>

        <div className='row gy-4'>
          {/* Extra Curriculum Cards */}
          {extraCurriculumData.map((certificate, index) => (
            <div key={index} className='col-md-6' data-aos='fade-up' data-aos-delay={index * 300}>
              <div className='card-custom rounded-4 bg-base shadow-effect'>
                <div className='card-custom-image rounded-4'>
                  <img className='rounded-4' src={certificate.image} alt={certificate.title} />
                </div>
                <div className='card-custom-content p-4'>
                  <h4>{certificate.title}</h4>
                  <p>{certificate.description}</p>
                  <a href={certificate.link} className='custom-link'>{certificate.linkText}</a>
                </div>
              </div>
            </div>
          ))}
        </div>

      </div>
    </section>
  );
};

const certificateData = [
  {
    title: 'MERN Stack',
    image: './assert/image/educational-certificate/educational-certificate-1.pdf',
    description: 'This is my MERN stack certificate. In this 6-month course, I have learned many things and it was a wonderful session ever.',
    link: 'https://www.roadtocode.org',
    linkText: 'Road to Code'
  },
  {
    title: 'Web Development',
    image: './assert/image/educational-certificate/educational-certificate-2.pdf',
    description: 'This is my Web Development certificate. In this 1-month course, I have learned HTML, CSS, PostgreSQL, PHP, and JavaScript.',
    link: 'https://www.skillvertex.com',
    linkText: 'Skill Vertex'
  },
];

const extraCurriculumData = [
  {
    title: 'Nandi Foundation Tech Mahindra',
    image: './assert/image/extra-curriculum/nandi-fondation.jpg',
    description: 'I have completed 7 Days of Employability Skill Programme where I learned skills for cracking interviews for jobs or internships.',
    link: 'https://ssssamritsar.edu.in/employability-skills-by-mahindra-pride-classroom',
    linkText: 'Mahindra Pride Classroom'
  },
  {
    title: 'IP Awareness Training Program',
    image: './assert/image/extra-curriculum/nipam.png',
    description: 'Government approved certificate by the Government of India Ministry of Commerce and Industry.',
    link: 'https://ipindia.gov.in/newsdetail.htm?774',
    linkText: 'NIPAM'
  },
  // Add more extra curriculum certificates as needed
];

export default Certificate;
